    <script>
        $(document).ready(function() {
            $('[data-action="remove-modpack"]').click(function (event) {
                var self = $(this);
                event.preventDefault();
                swal({
                    type: 'warning',
                    title: 'Remove the modpack?',
                    showCancelButton: true,
                    allowOutsideClick: true,
                    closeOnConfirm: false,
                    confirmButtonText: 'Remove',
                    confirmButtonColor: '#d9534f',
                    showLoaderOnConfirm: true
                }, function () {
                    $.ajax({
                        method: 'DELETE',
                        url: '/admin/bagoucenter/settings/addon/mcversion/' + self.data('attr'),
                        headers: {
                            'X-CSRF-TOKEN': '{{ csrf_token() }}'
                        }
                    }).done(function () {
                        swal({
                            type: 'success',
                            title: 'Removed!',
                            text: 'Modpack removed sucessfully.'
                        });
                        self.parent().parent().slideUp();
                    }).fail(function (jqXHR) {
                        console.error(jqXHR);
                        swal({
                            type: 'error',
                            title: 'Whoops!',
                            text: 'An error occurred while attempting to remove this modpack.'
                        });
                    });
                });
            });
        });
    </script>
