import useSWR from 'swr';
import http, { PaginatedResult } from '@/api/http';
import { createContext, useContext } from 'react';
import { ServerContext } from '@/state/server';

interface ctx {
  page: number;
  setPage: (value: number | ((s: number) => number)) => void;
  searchFilter: string;
  type: string;
  setType: (value: string | ((s: string) => string)) => void;
  version: string;
  setVersion: (value: string | ((s: string) => string)) => void;
  loader: string;
  setLoader: (value: string | ((s: string) => string)) => void;
  setSearchFilter: (value: string | ((s: string) => string)) => void;
}

export const Context = createContext<ctx>({
  page: 1,
  setPage: () => 1,
  searchFilter: '',
  setSearchFilter: () => '',
  type: 'curseforge',
  setType: () => 'curseforge',
  version: '',
  setVersion: () => '',
  loader: '',
  setLoader: () => '',
});

export default () => {
  const { page, searchFilter, type, version, loader } = useContext(Context);
  const uuid = ServerContext.useStoreState((state) => state.server.data!.uuid);

  return useSWR<PaginatedResult<any>>(
    ['server:minecraftModpacks', page, searchFilter, type, version, loader],
    async () => {
      const { data } = await http.get(`/api/client/servers/${uuid}/mcmodpacks`, {
        params: { page, search: searchFilter, type, version, loader },
        timeout: 60000,
      });
      const totalpage = type === 'voidswrath' ? 0 : type === 'ftb' ? 7 : 1317;
      return {
        items: data || [],
        pagination: { total: 13170, count: data.length, perPage: 20, currentPage: page, totalPages: totalpage },
      };
    }
  );
};

